constexpr long long calc(long long n) {
    return (n == 1 || n == 0) ? 1 : calc(n - 1) * n;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    
    int n;
    cin >> n;
    cout << calc(n);
}
