#define int long long
int ans = LLONG_MAX;
int n, b;
int a[40];
void bacctracc(int prevsum, int i) {
    if(prevsum >= b) {
        ans = min(ans, prevsum - b);
    }
    if(i > n) return;
    bacctracc(prevsum + a[i], i + 1);
    bacctracc(prevsum, i + 1);
}

signed main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    cin >> n >> b;
    for(int i = 1; i <= n; i++) cin >> a[i];
    bacctracc(0, 1);
    cout << ans;
}
