const int maxprime = 1e5 + 5;
vector<int> primes;

// Sieve of Eratosthenes to generate primes up to maxprime
void sieve() {
    vector<bool> isprime(maxprime + 5, 1);
    isprime[1] = isprime[0] = 0;
    for (int i = 2; i * i <= maxprime; i++) {
        if (isprime[i]) {
            for (int j = i * i; j <= maxprime; j += i) {
                isprime[j] = false;
            }
        }
    }
    for (int i = 2; i <= maxprime; i++) {
        if (isprime[i]) primes.push_back(i);
    }
}

// Find the smallest prime divisor of k
int smallest_prime_divisor(int k) {
    for (int p : primes) {
        if (p * p > k) break; // Stop if p exceeds sqrt(k)
        if (k % p == 0) return p;
    }
    return k; // If no divisors found, k is prime
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    #ifndef DEBUG
    freopen("BAI1.INP", "r", stdin);
    freopen("BAI1.OUT", "w", stdout);
    #endif

    int k;
    cin >> k;

    sieve();

    int smallest_divisor = smallest_prime_divisor(k);
    cout << smallest_divisor << endl;

    int triplet_count = 0;
    vector<array<int, 3>> triplets;
    for (int i = 0; i < primes.size(); i++) {
        int target = k - primes[i];
        if (target <= 0) break;

        int left = i + 1, right = primes.size() - 1;

        while (left < right) {
            int sum = primes[left] + primes[right];
            if (sum == target) {
                triplet_count++;
                triplets.push_back({primes[i], primes[left], primes[right]});
                left++;
                right--;
            } else if (sum < target) {
                left++;
            } else {
                right--;
            }
        }
    }

    cout << triplet_count << endl;
    for(array<int, 3> triplet : triplets) {
        cout << triplet[0] << " " << triplet[1] << " " << triplet[2] << endl;
    }
    return 0;
}



