const int MAXN = 105;
int n, W;
int v[MAXN], w[MAXN];
namespace Subtask1 {
    bool check() {
        return n <= 20;
    }
    vector<pair<int, int>> s1, s2;
    void bacctracc1(int l, int r, int w_, int v_) {
        if(l > r) {
            s1.emplace_back(w_, v_);
            return;
        }
        bacctracc1(l + 1, r, w_ + w[l], v_ + v[l]);
        bacctracc1(l + 1, r, w_, v_);
    }
    void bacctracc2(int l, int r, int w_, int v_) {
        if(l > r) {
            s2.emplace_back(w_, v_);
            return;
        }
        bacctracc2(l + 1, r, w_ + w[l], v_ + v[l]);
        bacctracc2(l + 1, r, w_, v_);
    }
    void solve() {
        int mid = n / 2;
        bacctracc1(1, mid, 0, 0);
        bacctracc2(mid + 1, n, 0, 0);
        sort(s1.begin(), s1.end());
        sort(s2.begin(), s2.end());
        int x = s1.size(), y = s2.size();
        vector<int> prefmax1(x), prefmax2(y);
        prefmax1[0] = s1[0].second;
        prefmax2[0] = s2[0].second;
        for(int i = 1; i < x; i++) {
            prefmax1[i] = max(prefmax1[i - 1], s1[i].second);
        }
        for(int i = 1; i < y; i++) {
            prefmax2[i] = max(prefmax2[i - 1], s2[i].second);
        }
        int r = y - 1, ans = LLONG_MIN;
        for(int l = 0; l < x; l++) {
            
            while(r >= 0 and s1[l].first + s2[r].first > W) r--;
            if(r >= 0)
            ans = max(ans, prefmax1[l] + prefmax2[r]);
        }
        cout << ans;
    }
}

namespace Subtask2 {
    bool check() {
        if (n > 100 || W > 100000) return false;
        return false;
    }

    void solve() {
        vector<vector<int>> dp(n + 1, vector<int>(W + 1, 0));

        for (int i = 1; i <= n; i++) {
            for (int w_ = 0; w_ <= W; w_++) {
                if (w[i] <= w_) {
                    dp[i][w_] = max(dp[i][w_], dp[i - 1][w_ - w[i]] + v[i]);
                } else {
                    dp[i][w_] = dp[i - 1][w_];
                }
            }
        }

        cout << dp[n][W] << endl;
    }
}

namespace Subtask3 {
    bool check() {
        if (n > 100) return false;
        bool check = true;
        for (int i = 1; i <= n; i++) {
            check &= (v[i] <= 1000);
        }
        return check;
    }

    void solve() {

    }
}

int main() {
    cin >> n >> W;
    for (int i = 1; i <= n; i++) {
        cin >> w[i] >> v[i];
    }
    if (Subtask1::check()) Subtask1::solve();
    else if (Subtask2::check()) Subtask2::solve();
    else {
        Subtask3::solve();
    }
}
