#ifndef ONLINE_JUDGE
#define debug(...) std::cerr << __LINE__ << ": [", __DEBUG_UTIL__::printer(#__VA_ARGS__, __VA_ARGS__)
#define debugArr(...) std::cerr << __LINE__ << ": [", __DEBUG_UTIL__::printerArr(#__VA_ARGS__, __VA_ARGS__)
#else
#define debug(...)
#define debugArr(...)
#endif
int n, compsize;
int a[(int) 1e5 + 5], b[(int) 1e5 + 5], dp[(int) 1e5 + 5];
vector<vector<int>> bit, comp;
vector<int> cpr;
int compressnum(int a, vector<int> &cpr) {
    return lower_bound(cpr.begin(), cpr.end(), a) - cpr.begin() + 1;
}
void fakeupd(int x, int y) {
    for(; x <= compsize; x += x & -x) {
        comp[x].push_back(y);
    }
}
void fakeget(int x, int y) {
    for(; x > 0; x -= x & -x) {
        comp[x].push_back(y);
    }
}
void remake() {
    for(int i = 1; i <= compsize; i++) {
        vector<int> &x = comp[i];
        sort(x.begin(), x.end());
        x.resize(unique(x.begin(), x.end()) - x.begin());
        bit[i].resize(x.size() + 1, 0);
    }
}
void update(int x, int y, int z) {
    for(int a = x; a <= compsize; a += a & -a) {
        for(int b = compressnum(y, comp[a]); b <= comp[a].size(); b += b & -b) {
            bit[a][b] = max(bit[a][b], z);
        }
    }
}
int get(int x, int y) {
    int ans = 0;
    for(int a = x; a > 0; a -= a & -a) {
        for(int b = compressnum(y, comp[a]); b > 0; b -= b & -b) {
            ans = max(ans, bit[a][b]);
        }
    }
    return ans;
}
int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    cin >> n;
    for(int i = 1; i <= n; i++) {
        cin >> a[i];
        cpr.push_back(a[i]);
    }
    for(int i = 1; i <= n; i++) {
        cin >> b[i];
        cpr.push_back(b[i]);
    }
    sort(cpr.begin(), cpr.end());
    cpr.resize(unique(cpr.begin(), cpr.end()) - cpr.begin());
    compsize = cpr.size();
    for(int i = 1; i <= n; i++) {
        a[i] = compressnum(a[i], cpr);
    }
    for(int i = 1; i <= n; i++) {
        b[i] = compressnum(b[i], cpr);
    }
    bit.resize(cpr.size() + 5);
    comp.resize(cpr.size() + 5);
    debug("testing");
    for(int i = 1; i <= n; i++) {
        fakeupd(a[i], b[i]);
        fakeget(a[i] - 1, b[i] - 1);
    }
    debug("testing");
    remake();
    debug("testing");
    fill(dp + 1, dp + 1 + n, 1);
    for(int i = 1; i <= n; i++) {
        dp[i] = get(a[i] - 1, b[i] - 1) + 1;
        update(a[i], b[i], dp[i]);
    }
    debug(dp);
    cout << *max_element(dp + 1, dp + 1 + n);
}