#else
#define debug(...)
#define debugArr(...)
#endif

#define MOD 1000000007
#pragma GCC optimize("Ofast,unroll-loops,fast-math")
#define MAX_M 750
#define MAX_N 750
int a[MAX_M + 1][MAX_N + 1];  
long long dp[MAX_M + 1][MAX_N + 1];  
long long pref[MAX_M + 1][MAX_N + 1];
unordered_map<int, array<int, MAX_M + 5>> bit;
int add(int a, int b) {
    int c = a + b;
    if(c >= MOD) c -= MOD;
    return c;
}
int sub(int a, int b) {
    int c = a - b;
    if(c < 0) c += MOD;
    return c;
} 
void update(int ind, int idx, int v) {
    while(idx <= MAX_M) {
        bit[ind][idx] = add(bit[ind][idx], v);
        idx += (idx & (-idx));
    }
}
int query(int ind, int idx) {
    int ans = 0;
    while(idx > 0) {
        ans = add(ans, bit[ind][idx]);
        idx -= (idx & (-idx));
    }
    return ans;
}

signed main() {
    #ifdef ONLINE_JUDGE
    freopen("BOARD.inp", "r", stdin);
    freopen("BOARD.out", "w", stdout);
    #endif
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);

    int m, n, K;
    cin >> m >> n >> K;

    for (int i = 1; i <= m; i++) {
        for (int j = 1; j <= n; j++) {
            cin >> a[i][j];
        }
    }

    dp[1][1] = 1;
    pref[1][1] = 1;
    for (int i = 1; i <= m; i++) {
        for (int j = 1; j <= n; j++) {
            if(i == 1 and j == 1) continue;
            dp[i][j] = sub(pref[i - 1][j - 1], query(a[i][j], j - 1));
            //debug(i, j, dp[i][j], pref[i - 1][j - 1], query(a[i][j], j - 1));
        }
        for(int j = 1; j <= n; j++) {
            pref[i][j] = add(add(pref[i - 1][j], pref[i][j - 1]), sub(dp[i][j], pref[i - 1][j - 1]));
            update(a[i][j], j, dp[i][j]);
        }
    }
    debug(query(a[1][1], 1));
    debug(dp);/*
    for(int i = 1; i <= m; i++) {
        for(int j = 1; j <= n; j++) {
            cerr << pref[i][j][4] << ' ';
        }
        cerr << endl;
    }
    for(int i = 1; i <= m; i++) {
        for(int j = 1; j <= n; j++) {
            int k = 4;
            cerr <<  pref[i - 1][j][k] + pref[i][j - 1][k] - pref[i - 1][j][k] << ' ';
        }
        cerr << endl;
    }*/
    cout << dp[m][n] << endl;
    return 0;
}