void generate(string current, int open, int close, int n, vector<string>& results) {
    if (current.length() == 2 * n) {
        results.push_back(current);
        return;
    }

    if (open < n) {
        generate(current + '(', open + 1, close, n, results);
    }

    if (close < open) {
        generate(current + ')', open, close + 1, n, results);
    }
}

int main() {
    int N;
    cin >> N;

    vector<string> results;
    generate("", 0, 0, N, results);

    sort(results.begin(), results.end());

    for (const string& s : results) {
        cout << s << endl;
    }

    return 0;
}