int a[(int) 2e5 + 5];
int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    int n;
    cin >> n;
    int maxval = INT_MIN;
    for(int i = 1; i <= n; i++) {
        cin >> a[i];
        maxval = max(maxval , a[i]);
    }
    int ans = 0;
    sort(a + 1, a + 1 + n);
    for(int i = 1; i <= n; i++) {
        for(int j = 0; j <= maxval + a[i]; j += a[i]) {
            int x = *(lower_bound(a + 1, a + 1 + n, j) - 1);
            if(x < a[i]) continue;
            ans = max(ans, x % a[i]);
        }
    }
    cout << ans;
}
