int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);

    int n, k;
    cin >> n >> k;
    vector<int> a(n);
    for(int i = 0; i < n; i++) {
        cin >> a[i];
    }

    sort(a.begin(), a.end());

    int ans = 0;
    for(int i = 0; i < n; i++) {
        int target = k - a[i];
        auto it = upper_bound(a.begin(), a.end(), target);
        if (it != a.begin()) {
            int val = *(--it);
            if (val != a[i]) { // Ensure that the pair is distinct
                ans = max(ans, a[i] + val);
            }
        }
    }
    
    cout << ans << endl;
    return 0;
}
