int main() {
    int n, k;
    cin >> n >> k;
    
    vector<int> A(n);
    map<int, vector<int>> cnt;
    for (int i = 0; i < n; ++i) {
        cin >> A[i];
        cnt[A[i]].push_back(i);
    }

    for (int i = 0; i < n; ++i) {
        if(!cnt[A[i] - k].empty()) {
            for(auto j:cnt[A[i] - k]) {
                if(j != i) {
                    cout << i + 1 << ' ' << j + 1 << endl;
                    exit(0);
                }
            }
        }
    }
    
    cout << "0 0" << endl;
    return 0;
}
