void generate(int n, vector<int>& current, int start) {
    if (n == 0) {
        for (size_t i = 0; i < current.size(); ++i) {
            cout << current[i];
            if (i != current.size() - 1) cout << "+";
        }
        cout << endl;
        return;
    }

    for (int i = start; i <= n; ++i) {
        current.push_back(i);
        generate(n - i, current, i);
        current.pop_back();
    }
}

int main() {
    int n;
    cin >> n;

    vector<int> current;
    generate(n, current, 1);

    return 0;
}
