#ifndef ONLINE_JUDGE
#define debug(...) std::cerr << __LINE__ << ": [", __DEBUG_UTIL__::printer(#__VA_ARGS__, __VA_ARGS__)
#define debugArr(...) std::cerr << __LINE__ << ": [", __DEBUG_UTIL__::printerArr(#__VA_ARGS__, __VA_ARGS__)
#else
#define debug(...)
#define debugArr(...)
#endif
#define int long long
const int maxn = 1e6;
set<int> stor1[maxn], stor2[maxn];  
vector<int> cmp;
pair<pair<int, int>, int> querystor[maxn];
pair<int, int> stor[maxn];

int compressnum(int a) {
    return lower_bound(cmp.begin(), cmp.end(), a) - cmp.begin();
}

signed main() {
    ios_base::sync_with_stdio(0); 
    cin.tie(0); 
    cout.tie(0);
    #ifdef ONLINE_JUDGE
    freopen("TRIANGLE.INP", "r", stdin);
    freopen("TRIANGLE.OUT", "w", stdout);
    #endif
    int n, q;
    cin >> n >> q;

    for (int i = 1; i <= n; i++) {
        int a, b;
        cin >> a >> b;
        stor[i] = {a, b};
        cmp.push_back(a);
        cmp.push_back(b);
    }

    for (int i = 1; i <= q; i++) {
        int t, a, b;
        cin >> t >> a >> b;
        cmp.push_back(a);
        cmp.push_back(b);
        querystor[i] = make_pair(make_pair(a, b), t);
    }

    sort(cmp.begin(), cmp.end());
    cmp.resize(unique(cmp.begin(), cmp.end()) - cmp.begin());

    for (int i = 1; i <= n; i++) {
        int x = compressnum(stor[i].first);
        int y = compressnum(stor[i].second);
        stor1[x].insert(stor[i].second);
        stor2[y].insert(stor[i].first);
    }

    for (int i = 1; i <= q; i++) {
        int t = querystor[i].second;
        int a = compressnum(querystor[i].first.first);
        int b = compressnum(querystor[i].first.second);

        if(t == 1) {
            stor1[a].insert(querystor[i].first.second);
            stor2[b].insert(querystor[i].first.first);
        }
        else if(t == 2) {
            stor1[a].erase(querystor[i].first.second);
            stor2[b].erase(querystor[i].first.first);
        }
        else if (t == 3) {

            if (stor1[a].empty() || stor2[b].empty()) {
                cout << 0 << '\n';
                continue;
            }

            int bmin = *stor1[a].begin();
            int bmax = *stor1[a].rbegin();
            int amin = *stor2[b].begin();
            int amax = *stor2[b].rbegin();

            int ans = 0;
            ans =  max(abs(querystor[i].first.first - amin), abs(querystor[i].first.first - amax)) * 
                   max(abs(querystor[i].first.second - bmin), abs(querystor[i].first.second - bmax));

            cout << (int) ans / 2 << (((int) ans & 1) ? ".5" : "") << '\n';
        }
    }

    return 0;
}
