int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    #ifdef ONLINE_JUDGE
    freopen("TRIANGLE.INP", "r", stdin);
    freopen("TRIANGLE.OUT", "w", stdout);
    #endif
    #define int long long
    set<pair<int, int>> stor, stor1;
    int n, q;
    cin >> n >> q;

    for(int i = 1; i <= n; i++) {
        int a, b;
        cin >> a >> b;
        stor.insert({a, b});
        stor1.insert({b, a});
    }

    while(q--) {
        int x;
        cin >> x;

        if(x == 1) {
            int a, b;
            cin >> a >> b;
            stor.insert({a, b});
            stor1.insert({b, a});
        } else if(x == 2) {
            int a, b;
            cin >> a >> b;
            stor.erase({a, b});
            stor1.erase({b, a});
        } else if(x == 3) {
            int x, y; cin >> x >> y;
            long long maxho = 0, maxve = 0;
            
            auto it1 = stor.lower_bound({x, -1000000000});
            auto it2 = stor.lower_bound({x + 1, -1000000000});
            auto it3 = stor1.lower_bound({y, -1000000000});
            auto it4 = stor1.lower_bound({y + 1, -1000000000});   

            if(it1 != stor.end() && it1->first == x) maxho = max(maxho, y - it1->second);
            if(it2 != stor.begin()) it2 --;
            if(it2->first == x) maxho = max(maxho, it2->second - y);
            if(it3 != stor1.end() && it3->first == y) maxve = max(maxve, x - it3->second);
            if(it4 != stor1.begin()) it4 --;
            if(it4->first == y) maxve = max(maxve, it4->second - x);

            cout << maxve * maxho/2;
            if(maxve * maxho % 2) cout << ".5";
            cout << '\n';
        }
    }

    return 0;
}
