#include <immintrin.h>
using namespace std;
#pragma GCC optimize("Ofast,O3,fast-math")
#pragma GCC target("popcnt,avx,avx2")
inline __m128i _mm_div_epi32(__m128i a, __m128i b) {
    // Convert the 4 packed 32-bit integers to double-precision (64-bit)
    __m256d a_double = _mm256_cvtepi32_pd(a);
    __m256d b_double = _mm256_cvtepi32_pd(b);

    // Perform division on the doubles
    __m256d result_double = _mm256_div_pd(a_double, b_double);

    // Apply floor rounding
    __m256d floor_result = _mm256_floor_pd(result_double);

    // Convert the floored double results back to integers
    __m128i result = _mm256_cvtpd_epi32(floor_result);

    return result;
}
int l, r;
void print_m128i(__m128i v) {
    alignas(16) int32_t vals[4];
    _mm_store_si128(reinterpret_cast<__m128i*>(vals), v);
    std::cout << "Result: [";
    for (int i = 0; i < 4; ++i) {
        std::cout << vals[i];
        if (i < 3) std::cout << ", ";
    }
    std::cout << "]\n";
}
signed main() {
    long long l, r;
    cin >> l >> r;
    long long ans = 0;
    __m128i ind = _mm_setr_epi32(1, 2, 3, 4);
    __m128i indinc = _mm_set1_epi32(4);
    __m128i ones = _mm_set1_epi32(1);
    for (int i = 1; i <= r; i += 4) {
        __m128i a = _mm_set1_epi32(l);
        a = _mm_add_epi32(a, ind);
        a = _mm_sub_epi32(a, _mm_set1_epi32(1));
        a = _mm_div_epi32(a, ind);
        a = _mm_mullo_epi32(a, ind);
        __m128i end_cond = _mm_cmpgt_epi32(a, _mm_set1_epi32(r));
        int mask = _mm_movemask_ps(_mm_castsi128_ps(end_cond));

        for (int j = 0; j < 4; ++j) {
            if (!(mask & (1 << j))) {
                ans += i + j;
            }
        }

        ind = _mm_add_epi32(ind, indinc);
    }

    cout << ans;

    return 0;
}