#else
#define debug(...)
#define debugArr(...)
#endif
const long long MOD = 1e9 + 7;
const long long MOD1 = 1e9 + 9;
const int BASE = 373;
const int BASE1 = 313;

long long hashs[(int)1e6 + 5];
long long hashs1[(int)1e6 + 5];
long long powmod[(int)1e6 + 5];
long long powmod1[(int)1e6 + 5];

void calcpow() {
    powmod[0] = powmod1[0] = 1;
    for (int i = 1; i <= 1e6; i++) {
        powmod[i] = (powmod[i - 1] * BASE) % MOD;
        powmod1[i] = (powmod1[i - 1] * BASE1) % MOD1;
    }
}

void calchash(string &s, long long hash[], long long hash1[]) {
    int n = s.size();
    for (int i = 1; i <= n; i++) {
        hash[i] = (hash[i - 1] * BASE + (int)s[i - 1]) % MOD;
        hash1[i] = (hash1[i - 1] * BASE1 + (int)s[i - 1]) % MOD1;
    }
}

pair<long long, long long> gethash(long long hash[], long long hash1[], int l, int r) {
    long long h1 = ((hash[r] - hash[l - 1] * powmod[r - l + 1]) % MOD + MOD) % MOD;
    long long h2 = ((hash1[r] - hash1[l - 1] * powmod1[r - l + 1]) % MOD1 + MOD1) % MOD1;
    return {h1, h2};
}

int main() {
    string s, t;
    cin >> s >> t;

    int n = s.size();
    int m = t.size();

    calcpow();

    calchash(t, hashs, hashs1);

    auto s1 = gethash(hashs, hashs1, 1, n);
    int cnt[255];
    int cnt1[255];
    memset(cnt, 0, sizeof cnt);
    memset(cnt1, 0, sizeof cnt1);

    for(int i = 1; i <= n; i++) {
        cnt[s[i - 1]] ++;
    }
    
    for(int i = 1; i <= n; i++) {
        cnt1[t[i - 1]]++;
    }
    bool isok = true;
    for(int i = 'a'; i <= 'z'; i++) {
        if(cnt[i] != cnt1[i]) {
            isok = false;
            break;
        }
    }
    set<pair<int, int>> stor;
    
    if(isok) {
        stor.insert(s1);
    }
    for(int i = n + 1; i <= m; i++) {
        cnt1[t[i - n - 1]]--;
        cnt1[t[i - 1]]++;
        bool isok = true;
        for(int i = 'a'; i <= 'z'; i++) {
            if(cnt[i] != cnt1[i]) {
                isok = false;
                break;
            }
        }
        if(!isok) continue;
        debug(i);
        pair<int, int> hash1 = gethash(hashs, hashs1, i - n + 1, i);
        stor.insert(hash1);
    }

    cout << stor.size();
    return 0;
}