struct mirror {
    bool ori;
    int r, c;
    int i;
    int next_idx[4];
};

const int maxn = 100000 + 5;
mirror a[maxn];
vector<mirror*> by_col[maxn], by_row[maxn];

enum dir { N = 0, E = 1, S = 2, W = 3 };

int dircnv(char d) {
    switch(d) {
        case 'N': return N;
        case 'E': return E;
        case 'S': return S;
        case 'O': return W;
    }
    return -1;
}
static constexpr int reflect_tbl[2][4] = {
    { E, N, W, S },
    { W, S, E, N }
};

pair<int,int> adj[maxn][4];
int visited[maxn][4], memo[maxn][4];

// unchanged
int dfs(int i, int d) {
    if (i == -1) return 0;
    if (visited[i][d] == 1) return memo[i][d] = -1;
    if (visited[i][d] == 2) return memo[i][d];
    visited[i][d] = 1;
    auto [j, d2] = adj[i][d];
    int res = dfs(j, d2);
    memo[i][d] = (res == -1 ? -1 : res + 1);
    visited[i][d] = 2;
    return memo[i][d];
}

int find_next(int r, int c, int d) {
    if (d == N) {
        auto &v = by_col[c];
        auto it = upper_bound(v.begin(), v.end(), r,
                              [](int val, const mirror* m){ return val < m->r; });
        return it == v.end() ? -1 : (*it)->i;
    }
    if (d == S) {
        auto &v = by_col[c];
        auto it = lower_bound(v.begin(), v.end(), r,
                              [](const mirror* m, int val){ return m->r < val; });
        if (it == v.begin()) return -1;
        --it;
        return (*it)->i;
    }
    if (d == E) {
        auto &v = by_row[r];
        auto it = upper_bound(v.begin(), v.end(), c,
                              [](int val, const mirror* m){ return val < m->c; });
        return it == v.end() ? -1 : (*it)->i;
    }
    // W
    auto &v = by_row[r];
    auto it = lower_bound(v.begin(), v.end(), c,
                          [](const mirror* m, int val){ return m->c < val; });
    if (it == v.begin()) return -1;
    --it;
    return (*it)->i;
}

int main(){
    ios::sync_with_stdio(false);
    cin.tie(0);

    int n, F, C;
    cin >> n >> F >> C;
    for(int i = 1; i <= n; i++){
        char x;
        cin >> x >> a[i].r >> a[i].c;
        a[i].ori = (x == '\\');
        a[i].i   = i;
        by_col[a[i].c].push_back(&a[i]);
        by_row[a[i].r].push_back(&a[i]);
    }
    for(int c = 1; c <= C; c++){
        auto &col = by_col[c];
        if(col.empty()) continue;
        sort(col.begin(), col.end(),
             [](const mirror* A, const mirror* B){ return A->r < B->r; });
        // N pointers
        for(int k = 0; k + 1 < (int)col.size(); k++)
            col[k]->next_idx[N] = col[k+1]->i;
        col.back()->next_idx[N] = -1;
        // S pointers
        for(int k = (int)col.size() - 1; k >= 1; k--)
            col[k]->next_idx[S] = col[k-1]->i;
        col.front()->next_idx[S] = -1;
    }
    for(int r = 1; r <= F; r++){
        auto &row = by_row[r];
        if(row.empty()) continue;
        sort(row.begin(), row.end(),
             [](const mirror* A, const mirror* B){ return A->c < B->c; });
        // E pointers
        for(int k = 0; k + 1 < (int)row.size(); k++)
            row[k]->next_idx[E] = row[k+1]->i;
        row.back()->next_idx[E] = -1;
        // W pointers
        for(int k = (int)row.size() - 1; k >= 1; k--)
            row[k]->next_idx[W] = row[k-1]->i;
        row.front()->next_idx[W] = -1;
    }
    for(int i = 1; i <= n; i++){
        for(int d = 0; d < 4; d++){
            int od = reflect_tbl[a[i].ori][d];
            int j  = a[i].next_idx[od];
            adj[i][d] = { j, od };
        }
    }
    for(int i = 1; i <= n; i++)
        for(int d = 0; d < 4; d++)
            if(!visited[i][d])
                dfs(i, d);

    int E; 
    cin >> E;
    while(E--){
        char cd; int r0, c0;
        cin >> cd >> r0 >> c0;
        int d0 = dircnv(cd);
        int j = find_next(r0, c0, d0);
        if(j == -1) cout << 0 << "\n";
        else        cout << dfs(j, d0) << "\n";
    }
}
