#define int long long
signed main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);

    int n, m;
    cin >> n >> m;

    vector<vector<int>> a(n + 1, vector<int>(m + 1));
    for (int i = 1; i <= n; ++i) {
        for (int j = 1; j <= m; ++j) {
            cin >> a[i][j];
        }
    }

    int q;
    cin >> q;

    struct Query {
        int x1, y1, x2, y2, c;
    };
    vector<Query> queries(q);
    for (int i = 0; i < q; ++i) {
        cin >> queries[i].x1 >> queries[i].y1 >> queries[i].x2 >> queries[i].y2 >> queries[i].c;
    }

    vector<vector<int>> diff(n + 2, vector<int>(m + 2, 0));

    for (const auto& query : queries) {
        diff[query.x1][query.y1] += query.c;
        diff[query.x1][query.y2 + 1] -= query.c;
        diff[query.x2 + 1][query.y1] -= query.c;
        diff[query.x2 + 1][query.y2 + 1] += query.c;
    }

    for (int i = 1; i <= n; ++i) {
        for (int j = 1; j <= m; ++j) {
            diff[i][j] += diff[i - 1][j] + diff[i][j - 1] - diff[i - 1][j - 1];
        }
    }
    for(int i = 1; i <= n; i++) {
        for(int j = 1; j <= m; j++) {
            diff[i][j] += a[i][j];
        }
    }
    vector<int> prefmaxr(n + 2, LLONG_MIN), prefmaxc(m + 2, LLONG_MIN);
    vector<int> suffmaxr(n + 2, LLONG_MIN), suffmaxc(m + 2, LLONG_MIN);

    for(int i = 1; i <= n; i++) {
        for(int j = 1; j <= m; j++) {
            prefmaxr[i] = max({prefmaxr[i], prefmaxr[i - 1], diff[i][j]});
            prefmaxc[j] = max({prefmaxc[j], prefmaxc[j - 1], diff[i][j]});
        }
    }
    for(int i = n; i >= 1; i--) {
        for(int j = m; j >= 1; j--) {
            suffmaxr[i] = max({suffmaxr[i], suffmaxr[i + 1], diff[i][j]});
            suffmaxc[j] = max({suffmaxc[j], suffmaxc[j + 1], diff[i][j]});
        }
    }
    debug(prefmaxr);
    debug(prefmaxc);
    int max_ = 0;
    for(int i = 1; i <= n; i++) {
        for(int j = 1; j <= m; j++) {
            max_ = max(max_, diff[i][j]);
        }
    }
    int min_w = LLONG_MAX;

    for (int i = 0; i < q; ++i) {
        const auto& query = queries[i];
        int x1 = query.x1, y1 = query.y1, x2 = query.x2, y2 = query.y2;

        int ans = 0;

        if (y1 > 1) ans = max(ans, prefmaxc[y1 - 1]);
        if (x1 > 1) ans = max(ans, prefmaxr[x1 - 1]);
        if (x2 < n) ans = max(ans, suffmaxr[x2 + 1]);
        if (y2 < m) ans = max(ans, suffmaxc[y2 + 1]);

        ans = max(ans, max(max_ - query.c, ans));

        min_w = min(min_w, ans);
    }

    cout << min_w << "\n";

    return 0;
}
