class LazySegmentTree {
private:
    int n;
    vector<int> tree, lazy;

    void build(const vector<int> &arr, int node, int start, int end) {
        if (start == end) {
            tree[node] = arr[start];
        } else {
            int mid = (start + end) / 2;
            build(arr, 2 * node, start, mid);
            build(arr, 2 * node + 1, mid + 1, end);
            tree[node] = min(tree[2 * node], tree[2 * node + 1]);
        }
    }

    void propagate(int node, int start, int end) {
        if (lazy[node] != 0) {

            tree[node] = max(tree[node] - lazy[node], 0);  

            if (start != end) {
                lazy[2 * node] += lazy[node];
                lazy[2 * node + 1] += lazy[node];
            }

            lazy[node] = 0;
        }
    }

    int range_min_query(int node, int start, int end, int l, int r) {
        propagate(node, start, end);

        if (start > r || end < l) {
            return INT_MAX;
        }

        if (start >= l && end <= r) {
            return tree[node];
        }

        int mid = (start + end) / 2;
        int left_min = range_min_query(2 * node, start, mid, l, r);
        int right_min = range_min_query(2 * node + 1, mid + 1, end, l, r);
        return min(left_min, right_min);
    }

    void range_decrement(int node, int start, int end, int l, int r, int value) {
        propagate(node, start, end);

        if (start > r || end < l) {
            return;
        }

        if (start >= l && end <= r) {
            lazy[node] += value;
            propagate(node, start, end);
            return;
        }

        int mid = (start + end) / 2;
        range_decrement(2 * node, start, mid, l, r, value);
        range_decrement(2 * node + 1, mid + 1, end, l, r, value);
        tree[node] = min(tree[2 * node], tree[2 * node + 1]);
    }
    void mark(int node, int start, int end, int idx) {
        propagate(node, start, end);
        if(start == end) {
            tree[node] = INT_MAX;
        } else {
            int mid = (start + end) / 2;
            if(idx <= mid) {
                mark(2 * node, start, mid, idx);
            } else {
                mark(2 * node + 1, mid + 1, end, idx);
            }
            tree[node] = min(tree[2 * node], tree[2 * node + 1]);
        }
    }
public:
    LazySegmentTree(const vector<int> &arr) {
        n = arr.size();
        tree.resize(4 * n, 0);
        lazy.resize(4 * n, 0);
        build(arr, 1, 0, n - 1);
    }

    int range_min_query(int l, int r) {
        return range_min_query(1, 0, n - 1, l, r);
    }

    void range_decrement(int l, int r, int value) {
        range_decrement(1, 0, n - 1, l, r, value);
    }
    void mark(int idx) {
        mark(1, 0, n - 1, idx);
    }
};

int find_first_zero(LazySegmentTree &segTree, int start, int end) {
    int l = start, r = end, ans = -1;
    while (l <= r) {
        int mid = (l + r) / 2;

        if (segTree.range_min_query(0, mid) == 0) {
            ans = mid;
            r = mid - 1;
        } else {
            l = mid + 1;
        }
    }
    return ans;
}
#include <ext/pb_ds/assoc_container.hpp>
#include <ext/pb_ds/tree_policy.hpp>

using namespace std;
using namespace __gnu_pbds;
typedef tree<int, null_type, less<int>, rb_tree_tag, tree_order_statistics_node_update> ordered_set;
int main() {
    int n;
    cin >> n;
    vector<int> a(n), t(n);

    for (int i = 0; i < n; i++) cin >> t[i];  
    for (int i = 0; i < n; i++) cin >> a[i];  
    ordered_set tree;
    vector<int> result(n);
    for (int i = 0; i < n; i++) {

        result[t[i] - 1] = tree.size() - tree.order_of_key(t[i]);
        tree.insert(t[i]);
    }

    for (int i : result) {
        cout << i << ' ';
    }
    cout << endl;
    LazySegmentTree segg(a);
    vector<int> ans(n);

    for (int i = 0; i < n; i++) {
        ans[i] = find_first_zero(segg, 0, n - 1);
        segg.range_decrement(ans[i], ans[i], a[i]);  
        segg.mark(ans[i]);
        debug(ans[i]);
        if (ans[i] != -1) {
            segg.range_decrement(0, ans[i], 1);  
        }
    }

    for (int i : ans) cout << i + 1 << ' ';  
    cout << endl;
    return 0;
}