unsigned long long countZeroesInFactorial(unsigned long long n) {
    unsigned long long count = 0;
    while (n >= 5) {
        count += n / 5;
        n /= 5;
    }
    return count;
}

int main() {
    unsigned long long N;
    cin >> N;

    unsigned long long totalZeros = 0;
    
    for (unsigned long long i = 1; i <= N; ++i) {
        totalZeros += countZeroesInFactorial(i);
    }

    cout << totalZeros << endl;
    return 0;
}