struct Edge {
    int vertex1, vertex2;
    Edge(int vertex1, int vertex2) : vertex1(vertex1), vertex2(vertex2) {}
    Edge() {}
};

struct Point {
    int row, col;
    Point(int row, int col) : row(row), col(col) {}
    Point() {}
};

struct GridQuery {
    vector<vector<int>> indices_by_row;
    vector<vector<int>> indices_by_col;

    GridQuery(const vector<Point>& point_list, int number_rows, int number_cols) {
        indices_by_row.resize(number_rows + 1);
        indices_by_col.resize(number_cols + 1);

        for (int index = 0; index < (int)point_list.size(); ++index) {
            int row = point_list[index].row;
            int col = point_list[index].col;
            indices_by_row[row].push_back(index);
            indices_by_col[col].push_back(index);
        }
    }

    const vector<int>& get_row_indices(const Point& point) const {
        return indices_by_row[point.row];
    }

    const vector<int>& get_col_indices(const Point& point) const {
        return indices_by_col[point.col];
    }
};

vector<vector<int>> build_adjacency_list(int total_points, const vector<Edge>& edge_list) {
    vector<vector<int>> adjacency_list(total_points);
    for (auto &edge : edge_list) {
        adjacency_list[edge.vertex1].push_back(edge.vertex2);
        adjacency_list[edge.vertex2].push_back(edge.vertex1);
    }
    return adjacency_list;
}

bool is_safe_to_color(int vertex, int color, const vector<vector<int>> &adjacency_list, const vector<int> &assigned_colors) {
    for (int neighbor : adjacency_list[vertex]) {
        if (assigned_colors[neighbor] == color) {
            return false;
        }
    }
    return true;
}

bool color_graph_recursive(int vertex, int max_colors, const vector<vector<int>> &adjacency_list, vector<int> &assigned_colors, int total_points) {
    if (vertex == total_points) {
        return true;
    }

    for (int current_color = 1; current_color <= max_colors; current_color++) {
        if (is_safe_to_color(vertex, current_color, adjacency_list, assigned_colors)) {
            assigned_colors[vertex] = current_color;
            if (color_graph_recursive(vertex + 1, max_colors, adjacency_list, assigned_colors, total_points)) {
                return true;
            }
            assigned_colors[vertex] = 0; // backtrack
        }
    }
    return false;
}

int find_minimum_colors(int total_points, const vector<Edge>& edge_list, vector<int>& assigned_colors) {
    vector<vector<int>> adjacency_list = build_adjacency_list(total_points, edge_list);
    assigned_colors.assign(total_points, 0);
    for (int colors_needed = 1; colors_needed <= total_points; colors_needed++) {
        if (color_graph_recursive(0, colors_needed, adjacency_list, assigned_colors, total_points)) {
            return colors_needed;
        }
    }
    return total_points;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int num_columns, num_rows, num_points;
    cin >> num_rows >> num_columns >> num_points;
    vector<Edge> edge_list;
    vector<Point> point_list;

    for (int index = 0; index < num_points; index++) {
        int row_input, col_input;
        cin >> row_input >> col_input;
        point_list.push_back(Point(row_input, col_input));
    }

    GridQuery grid(point_list, num_rows, num_columns);

    // Build the graph: add edges between points in the same row and same column.
    for (int index = 0; index < num_points; index++) {
        // Same row
        for (int other_index : grid.indices_by_row[point_list[index].row]) {
            if (other_index != index) {
                edge_list.push_back(Edge(index, other_index));
            }
        }
        // Same column
        for (int other_index : grid.indices_by_col[point_list[index].col]) {
            if (other_index != index) {
                edge_list.push_back(Edge(index, other_index));
            }
        }
    }

    vector<int> assigned_colors;
    int minimum_colors = find_minimum_colors(num_points, edge_list, assigned_colors);

    cout << minimum_colors << "\n";
    for (int index = 0; index < num_points; index++) {
        cout << assigned_colors[index] << " ";
    }
    cout << "\n";

    return 0;
}
