int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    int n;
    cin >> n;
    vector<int> a(n + 1);
    for(int i = 1; i <= n; i++) {
        cin >> a[i];
    }
    int ans = 0;
    for(int j = 1; j <= n; j++) {
        int cnt1 = 0, cnt2 = 0;
        for(int i = 1; i <= j - 1; i++) {
            cnt1 += (a[i] < a[j]);
        }
        for(int i = j + 1; i <= n; i++) {
            cnt2 += (a[j] < a[i]);
        }
        ans += cnt1 * cnt2;
    }
    cout << ans;
}
