#define debug(...)
#define debugArr(...)
#endif
const int alphabet_size = 3; 

int char_to_index(char ch) {
    if(ch=='A') return 0;
    if(ch=='T') return 1;
    return 2; 
}

int main(){
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, q;
    cin >> n >> q;
    string sequence_a, sequence_b;
    cin >> sequence_a >> sequence_b;

    vector<vector<int>> prefix_sum_a(n+1, vector<int>(alphabet_size, 0));
    vector<vector<int>> prefix_sum_b(n+1, vector<int>(alphabet_size, 0));
    for (int i = 0; i < n; i++){
        for (int j = 0; j < alphabet_size; j++){
            prefix_sum_a[i+1][j] = prefix_sum_a[i][j];
            prefix_sum_b[i+1][j] = prefix_sum_b[i][j];
        }
        prefix_sum_a[i+1][char_to_index(sequence_a[i])]++; 
        prefix_sum_b[i+1][char_to_index(sequence_b[i])]++;
    }

    vector<vector<int>> prefix_mismatch(n+1, vector<int>(6, 0));
    for (int i = 0; i < n; i++){
        for (int g = 0; g < 6; g++){
            prefix_mismatch[i+1][g] = prefix_mismatch[i][g];
        }
        if(sequence_a[i] != sequence_b[i]){
            if(sequence_a[i]=='A' && sequence_b[i]=='T') prefix_mismatch[i+1][0]++;
            else if(sequence_a[i]=='T' && sequence_b[i]=='A') prefix_mismatch[i+1][1]++;
            else if(sequence_a[i]=='A' && sequence_b[i]=='C') prefix_mismatch[i+1][2]++;
            else if(sequence_a[i]=='C' && sequence_b[i]=='A') prefix_mismatch[i+1][3]++;
            else if(sequence_a[i]=='T' && sequence_b[i]=='C') prefix_mismatch[i+1][4]++;
            else if(sequence_a[i]=='C' && sequence_b[i]=='T') prefix_mismatch[i+1][5]++;
        }
    }

    while(q--){
        int left, right;
        cin >> left >> right;

        bool possible = true;
        for (int c = 0; c < alphabet_size; c++){
            int count_a = prefix_sum_a[right+1][c] - prefix_sum_a[left][c];
            int count_b = prefix_sum_b[right+1][c] - prefix_sum_b[left][c];
            if(count_a != count_b) {
                possible = false;
                break;
            }
        }
        if(!possible){
            cout << -1 << "\n";
            continue;
        }

        vector<int> mismatch_count(6, 0);
        for (int g = 0; g < 6; g++){
            mismatch_count[g] = prefix_mismatch[right+1][g] - prefix_mismatch[left][g];
        }

        int direct = min(mismatch_count[0], mismatch_count[1]) + min(mismatch_count[2], mismatch_count[3]) + min(mismatch_count[4], mismatch_count[5]);

        int remainder_1 = abs(mismatch_count[0] - mismatch_count[1]);
        int remainder_2 = abs(mismatch_count[2] - mismatch_count[3]);
        int remainder_3 = abs(mismatch_count[4] - mismatch_count[5]);
        int remainder = remainder_1 + remainder_2 + remainder_3;

        if(remainder % 3 != 0){
            cout << -1 << "\n";
            continue;
        }
        int answer = direct + 2 * (remainder / 3);
        cout << answer << "\n";
    }
    return 0;
}