#pragma GCC optimize("Ofast")
#define int long long
const long long mod = 1e9 + 7;
const int base = 31;
int powmod[(int) 2e6 + 5];
const int modsquared = mod * mod;
int n;
string s;
long long hashs[(int) 2e6 + 5];
void calchash() {
    for(int i = 1; i <= n; i++) {
        hashs[i] = (hashs[i - 1] * base + (int) s[i]) % mod;
    }
}

string getRange(const string &s, int l, int r) {
    return s.substr(l - 1, r - l + 1);
}
string getRange(int l, int r) {
    return s.substr(l, r - l + 1);
}
int calchash(int l, int r) {
    debug(getRange(l, r));
    return ((long long) hashs[r] - (long long) hashs[l - 1] * powmod[r - l + 1] + modsquared) % mod;
}
signed main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    powmod[0] = 1;
    for(int i = 1; i <= 2e6 + 1; i++) {
        powmod[i] = (base * powmod[i - 1]) % mod;
    }
    cin >> n;
    string z;
    cin >> z;
    s = " " + z;
    set<long long> solutions;
    int ans = -1;
    int len_t = n - 1; 
    calchash();
    for (int i = 1; i <= n; ++i) { 
        if (len_t % 2 != 0) continue;
        int mid = (n + 1) / 2;
        long long leftHash, rightHash;
        long long combinedHash;
        if (i <= mid) {
            leftHash = (i > 1 ? calchash(1, i - 1) : 0);
            rightHash = (i < mid ? calchash(i + 1, mid) : 0);
            combinedHash = (leftHash * powmod[mid - i] + rightHash) % mod;
        } else {
            leftHash = (i > mid ? calchash(mid, i - 1) : 0);
            rightHash = (i < n ? calchash(i + 1, n) : 0);
            combinedHash = (leftHash * powmod[n - (i + 1) + 1] + rightHash) % mod;
        }
        if (combinedHash == (i <= mid ? calchash(mid + 1, n) : calchash(1, n / 2))) {
            debug(i);
            ans = i;
            solutions.insert(combinedHash);
        }
    }

#ifdef DEBUG
    for (const auto &sol : solutions) {
        cerr << "Solution: " << sol << '\n';
    }
#endif

    if (solutions.empty()) {
        cout << "No Solution\n";
    } else if (solutions.size() > 1) {
        cout << "Multiple Solutions\n";
    } else {
        cout << (ans < (n + 1) / 2 ? getRange((n + 1) / 2 + 1, n) : getRange(1, n / 2)) << '\n';
    }

    return 0;
}
