int n, w;

const int maxn = 10005;
const int maxw = 5005;
int a[maxn], b[maxn];
vector<vector<pair<int, int>>> dp;
int res = INT_MAX;

void xuli() {
    int c1 = 0, c2 = 0, ans = 2;
    for (int i = 1; i <= n; i++) {
        if (b[i]) {
            if (c1 + a[i] <= w) c1 += a[i];
            else {
                c1 = a[i];
                ans++;
            }
        } else {
            if (c2 + a[i] <= w) c2 += a[i];
            else {
                c2 = a[i];
                ans++;
            }
        }
    }
    res = min(res, ans);
}

void ql(int x) {
    if (x > n) xuli();
    else {
        for (int i = 0; i <= 1; i++) {
            b[x] = i;
            ql(x + 1);
        }
    }
}

void sub1() {
    res = INT_MAX;
    ql(1);
    cout << res << '\n';
}

void sub2() {
    long long tong = 0;
    for (int i = 1; i <= n; i++) tong += a[i];
    cout << tong / 100 + bool(tong % 100) << '\n';
}

void sub3() {
    exit(1);
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cin >> w >> n;

    if (w >= maxw || n >= maxn) {
        if (n <= 20) sub1();
        else sub2();
        return 0;
    }

    dp.resize(n + 2, vector<pair<int, int>>(w + 2, {0x3f3f3f3f, 0x3f3f3f3f}));
    for (int i = 1; i <= n; i++) {
        cin >> a[i];
    }
    dp[0][0] = {2, 0};
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= w; j++) {
            auto [k, w2] = dp[i][j];
            if (j + a[i + 1] <= w)
                dp[i + 1][j + a[i + 1]] = min(dp[i + 1][j], dp[i][j]);
            dp[i + 1][a[i + 1]] = min(dp[i + 1][a[i + 1]], {k + 1, w2});
            if (w2 + a[i + 1] <= w)
                dp[i + 1][j] = min(dp[i + 1][j], {k, w2 + a[i + 1]});
            dp[i + 1][j] = min(dp[i + 1][j], {k + 1, a[i + 1]});
        }
    }
    int ans = 0x3f3f3f3f;
    for (int i = 0; i <= w; i++) {
        if (dp[n][i] != make_pair(0x3f3f3f3f, 0x3f3f3f3f)) {
            ans = min(ans, dp[n][i].first);
        }
    }
    cout << ans << '\n';
    return 0;
}
