int n, a, b;
vector<int> x;
map<int, bool> stor; // Tracks which cells are unpainted

bool check(int w) {
    // Create a DP table: dp[i][a][b]
    vector<vector<vector<bool>>> dp(n + 1, vector<vector<bool>>(a + 1, vector<bool>(b + 1, false)));
    dp[0][0][0] = true; // Base case: 0 cells painted with 0 brushes

    for (int i = 1; i <= n; i++) {
        for (int aa = 0; aa <= a; aa++) {
            for (int bb = 0; bb <= b; bb++) {
                if (stor[i]) { 
                    // Case: Cell needs painting
                    // Use a w-sized brush if possible
                    if (aa > 0) {
                        int prev_w = max(0, i - w); // Clamp index
                        dp[i][aa][bb] = dp[prev_w][aa - 1][bb];
                    }
                    // Use a 2w-sized brush if possible
                    if (bb > 0) {
                        int prev_2w = max(0, i - 2 * w); // Clamp index
                        dp[i][aa][bb] = dp[prev_2w][aa][bb - 1];
                    }
                } else {
                    // Case: Cell already painted, carry forward the state
                    dp[i][aa][bb] = dp[i - 1][aa][bb];
                }
            }
        }
    }

    // Check if we can paint all cells with the given `a` and `b`
    for (int aa = 0; aa <= a; aa++) {
        for (int bb = 0; bb <= b; bb++) {
            if (dp[n][aa][bb]) 
            return true;
        }
    }
    return false;
}

