#define int long long
int hashf[(int) 1e5 + 5];
int hashb[(int) 1e5 + 5];
int hashp[(int) 1e5 + 5];
const int mod = 1e9 + 7;
const int bas = 373;
string s;
int n;
long long binpow(long long a, long long b, long long m = mod) {
    a %= m;
    long long res = 1;
    while (b > 0) {
        if (b & 1)
            res = res * a % m;
        a = a * a % m;
        b >>= 1;
    }
    return res;
}
void prephash() {
    hashp[0] = 1;
    for(int i = 1; i <= 1e5; i++) {
        hashp[i] = hashp[i - 1] * bas % mod;
    }
    for(int i = 1; i <= n; i++) {
        hashf[i] = (hashf[i - 1] + s[i] * hashp[i]) % mod;
    }
    for(int i = n; i >= 1; i--) {
        hashb[i] = (hashb[i + 1] + s[i] * hashp[n - i + 1]) % mod;
    }
}
int gethashf(int l, int r) {
    return (hashf[r] - hashf[l - 1] + mod) * binpow(hashp[l - 1], mod - 2) % mod;
}
int gethashr(int l, int r) {
    return (hashb[l] - hashb[r + 1] + mod) * binpow(hashp[n - r], mod - 2) % mod;
}
bool ispalin(int l, int r) {return gethashf(l, r) == gethashr(l, r);}
int dp[1005];
signed main() {
    cin >> n;
    cin >> s;
    s = " " + s;
    prephash();
    memset(dp, 0x3f, sizeof dp);
    dp[0] = 0;
    for(int i = 1; i <= n; i++) {
        for(int j = 1; j <= i; j++) {
            if(ispalin(j, i)) {
                dp[i] = min(dp[i], dp[j - 1] + 1);
            }
        }
    }
    vector<int> ends;
    int ind = n;
    while(ind > 0) {
        for(int i = 1; i <= ind; i++) {
            if(ispalin(i, ind) and dp[ind] == dp[i - 1] + 1) {
                debug(i, ind);
                ends.push_back(ind);
                ind = i - 1;
                break;
            }
        }
    }
    reverse(ends.begin(), ends.end());
    cout << ends.size() << '\n';
    for(int i :ends) cout << i << '\n';
}