#ifndef ONLINE_JUDGE
#define debug(...) std::cerr << __LINE__ << ": [", __DEBUG_UTIL__::printer(#__VA_ARGS__, __VA_ARGS__)
#define debugArr(...) std::cerr << __LINE__ << ": [", __DEBUG_UTIL__::printerArr(#__VA_ARGS__, __VA_ARGS__)
#else
#define debug(...)
#define debugArr(...)
#endif
vector<int> tree[(int)1e5 + 5];
int h[(int)1e5 + 5], up[(int)1e5 + 5][20];

void dfs(int u) {
    for (int v : tree[u]) {
        if (v == up[u][0]) continue;
        h[v] = h[u] + 1;

        up[v][0] = u;
        for (int j = 1; j < 20; ++j)
            up[v][j] = up[up[v][j - 1]][j - 1];

        dfs(v);
    }
}
bool visited[(int) 1e5 + 5];
int in[(int) 1e5 + 5];
int out[(int) 1e5 + 5];
int timer = 0;
void dfs1(int u) {
    visited[u] = true;
    timer++;
    in[u] = timer;
    for(int i : tree[u]) {
        if(!visited[i]) dfs1(i);
    }
    timer++;
    out[u] = timer;
}
int lca(int u, int v) {
    if (h[u] != h[v]) {
        if (h[u] < h[v]) swap(u, v);

        int k = h[u] - h[v];
        for (int j = 0; (1 << j) <= k; ++j)
            if (k >> j & 1)
                u = up[u][j];
    }
    if (u == v) return u;

    int k = __lg(h[u]);
    for (int j = k; j >= 0; --j)
        if (up[u][j] != up[v][j])
            u = up[u][j], v = up[v][j];
    return up[u][0];
}
bool ischild(int u, int v) {
    return in[u] >= in[v] and out[u] <= out[v];
}
int distance(int u, int v) {
    return h[u] + h[v] - 2 * h[lca(u, v)];
}
bool cmp(pair<int, int> a, pair<int, int> b) {
    return distance(a.first, a.second) < distance(b.first, b.second);
}
int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);

    int n, m;
    cin >> n >> m;
    for (int i = 1; i <= n - 1; i++) {
        int a, b;
        cin >> a >> b;
        tree[a].push_back(b);
        tree[b].push_back(a);
    }

    dfs(1);
    dfs1(1);
    for (int i = 1; i <= m; i++) {
        int x, y, z;
        cin >> x >> y >> z;
        int lcayz = lca(y, z);
        if(ischild(x, lcayz)) {
            debug(distance(x, lca(y, x)), distance(x, lca(x, z)));
            cout << min(make_pair(x, lca(y, x)), make_pair(x, lca(x, z)), cmp).second << '\n';
        } else {
            debug(' ');
            cout << lcayz << '\n';
        }
    }
}
