#else
#define debug(...)
#define debugArr(...)
#endif

int main() {
    string n, m;
    cin >> n >> m;
    int a = n.size(), b = m.size();
    if(a < b) {
        sort(n.begin(), n.end(), greater<char>());
        cout << n;
        return 0;
    }
    reverse(m.begin(), m.end());
    while(a > b) {
        m.push_back('0');
        b++;
    }
    reverse(m.begin(), m.end());
    n = " " + n;
    m = " " + m;
    vector<int> tot(15, 0), cnt(15, 0);
    for(int i = 1; i <= b; i++) cnt[m[i] - '0']++;
    for(int i = 1; i <= a; i++) tot[n[i] - '0']++;
    string ans = "";
    for(int pos = a; pos >= 1; pos--) {
        debug(ans);
        bool ok = true;
        cnt[m[pos] - '0']--;
        for(int i = 9; i >= 0; i--) {
            if(cnt[i] > tot[i]) {
                ok = false;
            }
        }
        if(not ok) continue;
        for(int i = m[pos] - '0' - 1; i >= 0; i--) {
            if(tot[i] >= cnt[i] + 1) {
                for(int j = 1; j <= pos - 1; j++) {
                    ans.push_back(m[j]);
                    tot[m[j] - '0']--;
                }
                ans.push_back(i + '0');
                tot[i]--;
                for(int j = 9; j >= 0; j--) {
                    while(tot[j] > 0) {
                        ans.push_back(j + '0');
                        tot[j]--;
                    }
                }
                reverse(ans.begin(), ans.end());
                while(*ans.rbegin() == '0') ans.pop_back();
                reverse(ans.begin(), ans.end());
                cout << ans;
                return 0;
            }
        }
    }
    cout << -1 << '\n';
}