using namespace std;
#pragma GCC optimize("Ofast")
bool safe_multiply(long long a, long long b) {
    if (a == 0 || b == 0) return true;
    if (a > 1e18 / b) return false;
    return true;
}
vector<long long> gen(const vector<int>& primes, int k) {
    vector<long long> result = {1};
    for (int prime : primes) {
        vector<long long> temp = result; 
        for (long long prod : temp) {
            long long power = prime;
            for (int i = 1; i <= k; ++i) {
                if (!safe_multiply(prod, power)) break; 
                long long new_prod = prod * power;
                if (new_prod > 1e18) break;
                result.push_back(new_prod);
                if (!safe_multiply(power, prime)) break; 
                power *= prime;
            }
        }
    }
    sort(result.begin(), result.end());
    return result;
}

long long count_less_equal(const vector<long long>& a, const vector<long long>& b, long long value) {
    long long count = 0;
    for (long long x : a) {
        if (x > value) break;
        count += upper_bound(b.begin(), b.end(), value / x) - b.begin();
    }
    return count;
}

long long solve(int n, vector<int>& primes, int k) {
    long long limit = 1e18;

    vector<int> left_primes(primes.begin(), primes.begin() + n / 2);
    vector<int> right_primes(primes.begin() + n / 2, primes.end());

    vector<long long> a = gen(left_primes, k);
    vector<long long> b = gen(right_primes, k);
    if(a.size() > b.size()) swap(a, b);
    long long low = 1, high = limit, answer = -1;
    while (low <= high) {
        long long mid = (low + high) / 2;
        long long count = count_less_equal(a, b, mid);
        if (count >= k) {
            answer = mid;
            high = mid - 1;
        } else {
            low = mid + 1;
        }
    }
    return answer;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);

    int n, k;
    cin >> n;
    vector<int> primes(n);
    for (int i = 0; i < n; ++i) cin >> primes[i];
    cin >> k;

    cout << solve(n, primes, k) << '\n';
    return 0;
}