const int maxn = 2e5 + 5;
#define node pair<int, int>
node seg[4 * maxn];
const int inf = (1 << 30);
node merge(node l, node r) {
    node ans = {l.first + r.first, min({l.first + r.second, l.second})};
    return ans;
}

node query(int ql, int qr, int l, int r, int id) {
    if(qr < l or r < ql) return node(0, inf);
    if(ql <= l and r <= qr) {
        return seg[id];
    }
    int mid = (l + r) / 2;
    return merge(query(ql, qr, l, mid, id * 2), query(ql, qr, mid + 1, r, id * 2 + 1));
}

void update(int pos, int v, int l, int r, int id) {
    if(pos < l or r < pos) return;
    if(l == r) {
        seg[id] = node(seg[id].first + v, (seg[id].second + v));
        return;
    }
    int mid = (l + r) / 2;
    update(pos, v, l, mid, id * 2);
    update(pos, v, mid + 1, r, id * 2 + 1);
    seg[id] = merge(seg[id * 2], seg[id * 2 + 1]);
}

int a[maxn];
int b[maxn];

vector<int> cmp;
int compress(int a) {return lower_bound(cmp.begin(), cmp.end(), a) - cmp.begin() + 1;}

int main () {
    int n;
    cin >> n;
    for(int i = 1; i <= n; i++) {
        cin >> a[i];
        cmp.push_back(a[i]);
    }
    for(int i = 1; i <= n; i++) {
        cin >> b[i];
        cmp.push_back(b[i]);
    }
    sort(cmp.begin(), cmp.end());
    cmp.resize(unique(cmp.begin(), cmp.end()) - cmp.begin());
    for(int i = 1; i <= n; i++) {
        a[i] = compress(a[i]);
        b[i] = compress(b[i]);
    }
    int ans = 0;
    int maxx = cmp.size();
    for(int i = 1; i <= n; i++) {
        update(a[i], -1, 1, maxx, 1);
        update(b[i], 1, 1, maxx, 1);
        debug(seg[1]);
        if(seg[1].second >= 0) {
            ans = i;
        }
    }
    for(int i = 1; i <= maxx; i++) {
        debug(query(i, i, 1, maxx, 1));
    }
    cout << ans << '\n';
}