#define debugArr(...) std::cerr << __LINE__ << ": [", __DEBUG_UTIL__::printerArr(#__VA_ARGS__, __VA_ARGS__)
#else
#define debug(...)
#define debugArr(...)
#endif
#define int long long
int dp[1000][1000];
bool isprime[10000];
void sieve(int n) {
    memset(isprime, true, sizeof isprime);
    isprime[0] = isprime[1] = false;
    for (int p = 2; p * p <= n; ++p) {
        if (isprime[p]) {
            for (int i = p * p; i <= n; i += p) {
                isprime[i] = false;
            }
        }
    }

}

signed main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);

    memset(dp, 0, sizeof(dp));

    bool prime[1005];
    memset(prime, true, sizeof(prime));

    sieve(1000);

    for (int i = 1; i <= 9; i++)
        dp[1][i] = 1;

    for (int i = 2; i <= 900; i++) {
        for (int j = 0; j <= 900; j++) {
            for (int k = 0; k <= 9; k++) {
                dp[i][j + k] += dp[i - 1][j];
            }
        }
    }
    dp[0][1] = 1;
    int ans = 0;

    int q;
    cin >> q;
    while(q--) {
        int l, r;
        cin >> l >> r;
        int ans = 0;
        for(int i = l; i <= r; i++) {
            debug(i);
            for(int j = 2; j <= 900; j++) {
                if(isprime[j])
                ans += dp[i][j];
            }
        }
        cout << ans << '\n';
    }
}