int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    int n;
    cin >> n;
    vector<int> a(n + 1);
    int upperbound = INT_MIN;
    for(int i = 1; i <= n; i++) {
        cin >> a[i];
        upperbound = max(upperbound, a[i]);
    }
    vector<int> stor(2e6);
    for(int i = 1; i <= n; i++) {
        stor[a[i]]++;
    }
    int count = 0;
    for(long long a = 0; a <= upperbound; a++) {
        for(long long b = 0; b <= upperbound; b++) {
            count += (a != b and a != (long long) sqrt(a * a + b * b) and b != (long long) sqrt(a * a + b * b) and  stor[a] and stor[b] and stor[(long long) sqrt(a * a + b * b)]) ? stor[(long long) sqrt(a * a + b * b)] : 0;
        }
    }
    cout << count;
}
