class LazySegTree {
    int n;
    vector<int> tree, lazy;
    vector<bool> pendingUpdate;  

    void apply(int node, int start, int end) {
        if (pendingUpdate[node]) {
            tree[node] = lazy[node];  
            if (start != end) {
                lazy[2 * node + 1] = lazy[node];  
                lazy[2 * node + 2] = lazy[node];  
                pendingUpdate[2 * node + 1] = true;
                pendingUpdate[2 * node + 2] = true;
            }
            pendingUpdate[node] = false;  
        }
    }

    void build(const vector<int>& arr, int node, int start, int end) {
        if (start == end) {
            tree[node] = arr[start];
        } else {
            int mid = (start + end) / 2;
            build(arr, 2 * node + 1, start, mid);
            build(arr, 2 * node + 2, mid + 1, end);
            tree[node] = max(tree[2 * node + 1], tree[2 * node + 2]);
        }
    }

    void updateRange(int node, int start, int end, int l, int r, int val) {
        apply(node, start, end);

        if (start > end || start > r || end < l)
            return;

        if (start >= l && end <= r) {
            lazy[node] = val;
            pendingUpdate[node] = true;
            apply(node, start, end);
        } else {
            int mid = (start + end) / 2;
            updateRange(2 * node + 1, start, mid, l, r, val);
            updateRange(2 * node + 2, mid + 1, end, l, r, val);
            tree[node] = max(tree[2 * node + 1], tree[2 * node + 2]);
        }
    }

    int queryRange(int node, int start, int end, int l, int r) {
        apply(node, start, end);

        if (start > end || start > r || end < l)
            return 0;  

        if (start >= l && end <= r)
            return tree[node];

        int mid = (start + end) / 2;
        int leftQuery = queryRange(2 * node + 1, start, mid, l, r);
        int rightQuery = queryRange(2 * node + 2, mid + 1, end, l, r);
        return max(leftQuery, rightQuery);
    }

public:
    LazySegTree(const vector<int>& arr) {
        n = arr.size();
        tree.resize(4 * n);
        lazy.resize(4 * n, 0);
        pendingUpdate.resize(4 * n, false);
        build(arr, 0, 0, n - 1);
    }

    void updateRange(int l, int r, int val) {
        updateRange(0, 0, n - 1, l, r, val);
    }

    int queryRange(int l, int r) {
        return queryRange(0, 0, n - 1, l, r);
    }
};

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);

    int n, k, p;
    cin >> n >> k >> p;
    vector<int> a(2 * n + 1);
    for (int i = 1; i <= n; ++i) {
        cin >> a[i];
        a[i + n] = a[i];
    }

    vector<int> pref(2 * n + 1, 0);
    for (int i = 1; i <= n * 2; ++i) {
        pref[i] = pref[i - 1] + a[i];
    }

    LazySegTree segtree(vector<int>(2 * n + 1, 0));
    for (int i = k; i <= n * 2; ++i) {
        int range_sum = pref[i] - pref[i - k];  
        segtree.updateRange(i, i, range_sum);
    }
    debug(pref);
    for(int i  = 1; i <= n * 2; ++i) {
        debug(i, segtree.queryRange(i, i));
    }
    int curr = 1;  
    for (int i = 1; i <= p; ++i) {
        char q;
        cin >> q;
        if (q == '!') {
            curr--;
            if (curr < 1) {
                curr = n;
            }
        } else {  
            if(n < k) {
                k = min(n, k);
            } 
            cout << segtree.queryRange(curr + k - 1, curr + n - 1) << endl;  
        }
    }

    return 0;
}