class SegmentTree {
public:
    SegmentTree(int n) : n(n), tree(4 * n, INT_MAX) {}

    void update(int idx, int val, int node, int start, int end) {
        if (start == end) {
            tree[node] = val;
        } else {
            int mid = (start + end) / 2;
            if (idx <= mid) {
                update(idx, val, 2 * node + 1, start, mid);
            } else {
                update(idx, val, 2 * node + 2, mid + 1, end);
            }
            tree[node] = min(tree[2 * node + 1], tree[2 * node + 2]);
        }
    }

    int query(int L, int R, int node, int start, int end) {
        if (R < start || L > end) {
            return INT_MAX;
        }
        if (L <= start && end <= R) {
            return tree[node];
        }
        int mid = (start + end) / 2;
        int left_query = query(L, R, 2 * node + 1, start, mid);
        int right_query = query(L, R, 2 * node + 2, mid + 1, end);
        return min(left_query, right_query);
    }

    void update(int idx, int val) {
        update(idx, val, 0, 0, n - 1);
    }

    int query(int L, int R) {
        return query(L, R, 0, 0, n - 1);
    }

private:
    int n;
    vector<int> tree;
};

int main() {
    cin.tie(0);
    string s;
    cin >> s;
    int n = s.size();
    s = " " + s + s;  
    vector<int> pref(2 * n + 1);

    SegmentTree seg(2 * n + 5);
    for (int i = 1; i <= 2 * n; i++) {
        pref[i] = pref[i - 1] + (s[i] == '(' ? 1 : -1);
        seg.update(i, pref[i]);
    }
    int q;
    cin >> q;
    while(q--) {
        int l, r;
        cin >> l >> r;
        if(seg.query(l, r) >= pref[l - 1] && pref[r] - pref[l - 1] == 0) {
            cout << "YES";
        } else {
            cout << "NO";
        }
        cout << "\n";
    }
}
