#define int long long
const int MOD = 1e9 + 7;
int calc(int n) {
    int a = n;
    int b = (n + 1);
    int c = (2 * n + 1);
    if(a % 2 == 0) {
        a /= 2;
    } else if(b % 2 == 0) {
        b /= 2;
    } else c /= 2;
    if(a % 3 == 0) a /= 3;
    else if (b % 3 == 0) b /= 3;
    else c /= 3;
    return ((a * b) % MOD * c) % MOD;
}
signed main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    int n;
    cin >> n;
    int x = sqrt(n * 2);
    if(x * (x + 1) < n * 2) x++;
    int r = n - (x - 1 + 1) * (x - 1) / 2;
    int ans = calc(x - 1) + r * x % MOD;
    ans %= MOD;
    cout << ans;
}
