class LazySegTree {
    int n;
    std::vector<int> tree, lazy;
    std::vector<bool> pendingUpdate;  // To track if there's a pending update

    // Apply the lazy value to a node
    void apply(int node, int start, int end) {
        if (pendingUpdate[node]) {
            tree[node] = lazy[node];  // Set the node to the lazy value
            if (start != end) {
                lazy[2 * node + 1] = lazy[node];  // Set left child to the lazy value
                lazy[2 * node + 2] = lazy[node];  // Set right child to the lazy value
                pendingUpdate[2 * node + 1] = true;
                pendingUpdate[2 * node + 2] = true;
            }
            pendingUpdate[node] = false;  // Clear the lazy value
        }
    }

    // Recursively build the segment tree
    void build(const std::vector<int>& arr, int node, int start, int end) {
        if (start == end) {
            tree[node] = arr[start];
        } else {
            int mid = (start + end) / 2;
            build(arr, 2 * node + 1, start, mid);
            build(arr, 2 * node + 2, mid + 1, end);
            tree[node] = std::max(tree[2 * node + 1], tree[2 * node + 2]);
        }
    }

    // Update a range of values with lazy propagation
    void updateRange(int node, int start, int end, int l, int r, int val) {
        apply(node, start, end);

        if (start > end || start > r || end < l)
            return;

        if (start >= l && end <= r) {
            lazy[node] = val;
            pendingUpdate[node] = true;
            apply(node, start, end);
        } else {
            int mid = (start + end) / 2;
            updateRange(2 * node + 1, start, mid, l, r, val);
            updateRange(2 * node + 2, mid + 1, end, l, r, val);
            tree[node] = std::max(tree[2 * node + 1], tree[2 * node + 2]);
        }
    }

    // Query the maximum value in a range
    int queryRange(int node, int start, int end, int l, int r) {
        apply(node, start, end);

        if (start > end || start > r || end < l)
            return -1e9;  // Return negative infinity for invalid range

        if (start >= l && end <= r)
            return tree[node];

        int mid = (start + end) / 2;
        int leftQuery = queryRange(2 * node + 1, start, mid, l, r);
        int rightQuery = queryRange(2 * node + 2, mid + 1, end, l, r);
        return std::max(leftQuery, rightQuery);
    }

public:
    LazySegTree(const std::vector<int>& arr) {
        n = arr.size();
        tree.resize(4 * n);
        lazy.resize(4 * n, 0);
        pendingUpdate.resize(4 * n, false);
        build(arr, 0, 0, n - 1);
    }

    // Public method to update a range
    void updateRange(int l, int r, int val) {
        updateRange(0, 0, n - 1, l, r, val);
    }

    // Public method to query a range
    int queryRange(int l, int r) {
        return queryRange(0, 0, n - 1, l, r);
    }
};
struct shelf {
    int l, r, h;
    bool operator<(const shelf& other) const {
        return h < other.h;
    }
};
shelf constr(int l, int r, int h) {
    shelf tmp;
    tmp.l = l;
    tmp.r = r;
    tmp.r = h;
    return tmp;
}
int compressnum(int a, vector<int> &cpr) {
    return lower_bound(cpr.begin(), cpr.end(), a) - cpr.begin() + 1;
}
set<shelf> stor;
shelf a[(int) 1e5 + 5];
vector<int> comp;
signed main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    int n;
    cin >> n;
    for(int i = 1; i <= n; i++) {
        cin >> a[i].l >> a[i].r >> a[i].h;
        comp.push_back(a[i].l);
        comp.push_back(a[i].r);
    }
    sort(comp.begin(), comp.end());
    LazySegTree segtree(vector<int>(comp.size() + 10));
    comp.resize(unique(comp.begin(), comp.end()) - comp.begin());
    for(int i = 1; i <= n; i++) {
        a[i].l = compressnum(a[i].l, comp);
        a[i].r = compressnum(a[i].r, comp);
    }
    sort(a + 1, a + 1 + n);
    int ans = 0;
    for(int i = 1; i <= n; i++) {
        int l = segtree.queryRange(a[i].l, a[i].l);
        int r = segtree.queryRange(a[i].r, a[i].r);
        ans += a[i].h - l + a[i].h - r;
        debug(l, r);
        segtree.updateRange(a[i].l, a[i].r, a[i].h);
    }
    cout << ans;
}