#endif

int root[(int) 3e5 + 5], f[(int) 3e5 + 5], s[(int) 3e5 + 5];
int m, n, t;
void add_set(int u) {
    root[u] = u;
}
int get_root(int u) {
    if(root[u] == u) {
        return u;
    }
    //debug(u);
    return (root[u] = get_root(root[u]));
}
void unite(int u, int v) {
    int a, b;
    a = get_root(u);
    b = get_root(v);
    if(a != b) {
        root[a] = b;
        s[b] += s[a];
        f[b] += f[a];
    }
}
int getcoord(int x, int y) {
    return (x - 1) * n + y;
}
const int dx[] = {0, 1};
const int dy[] = {1, 0};
struct connect {
    int a, b, diff;
};
bool cmp(const connect &a, const connect &b) {
    return a.diff < b.diff;
}
int a[505][505];
bool isstart[505][505];
int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    cin >> m >> n >> t;
    for(int i = 1; i <= m; i++) {
        for(int j = 1; j <= n; j++) {
            cin >> a[i][j];
        }
    }
    for(int i = 1; i <= m; i++) {
        for(int j = 1; j <= n; j++) {
            cin >> isstart[i][j];
        }
    }
    vector<connect> stor;
    for(int i = 1; i <= m; i++) {
        for(int j = 1; j <= n; j++) {
            for(int x = 0; x < 2; x++) {
                if(i + dx[x] <= m and j + dy[x] <= n) {
                    connect tmp;
                    tmp.a = getcoord(i, j);
                    tmp.b = getcoord(i + dx[x], j + dy[x]);
                    tmp.diff = abs(a[i][j] - a[i + dx[x]][j + dy[x]]);
                    
                    stor.push_back(tmp);
                }
            }
        }
    }
    sort(stor.begin(), stor.end(), cmp);
    for(int i = 1; i <= m; i++) {
        for(int j = 1; j <= n; j++) {
            add_set(getcoord(i, j));
            if(isstart[i][j])
            f[getcoord(i, j)] = 1;
            
            s[getcoord(i, j)] = 1;
        }
    }
    
    long long ans = 0;
    for(connect a:stor) {
        int x = get_root(a.a);
        int y = get_root(a.b);
        debug(x, y);
        debug(f[x], f[y]);
        debug(s[x], s[y]);
        debug(a.diff);
        if(s[x] + s[y] >= t and x != y) {
            if(s[x] < t) ans += 1LL * a.diff * f[x];
            if(s[y] < t) ans += 1LL * a.diff * f[y];
        }
        unite(x, y);
    }
    cout << ans;
}
