int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    int n;
    vector<int> pref(1e6 + 5);
    string S;
    cin >> S;
    n = S.size();
    S = ' ' + S;
    for(int i = 1; i <= n; i++) {
        pref[i] = (pref[i - 1] + (S[i] - '0')) % 3;
    }
    vector<int> cnt(5);
    int ans = 0;
    for(int j = 1; j <= n; j++) {
        if(S[j] != '0') cnt[pref[j - 1]] ++;
        ans += cnt[pref[j]];
        //cout << ans  << endl;;
    }
    cout << ans;
}