long long calc(const vector<vector<long long>>& pref, int x1, int y1, int x2, int y2) {
    x1 = max(1, x1);
    y1 = max(1, y1);
    x2 = min((int)pref.size() - 1, x2);
    y2 = min((int)pref[0].size() - 1, y2);
    return pref[x2][y2] - pref[x1 - 1][y2] - pref[x2][y1 - 1] + pref[x1 - 1][y1 - 1];
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);

    int n, k;
    cin >> n >> k;
    vector<vector<long long>> a(n + 1, vector<long long>(n + 1, 0));
    vector<vector<long long>> ans(n + 1, vector<long long>(n + 1, 0));

    for (int i = 1; i <= n; ++i) {
        for (int j = 1; j <= n; ++j) {
            cin >> a[i][j];
        }
    }

    int size = 2 * n; 
    vector<vector<long long>> transformed(size + 1, vector<long long>(size + 1, 0));
    for (int i = 1; i <= n; ++i) {
        for (int j = 1; j <= n; ++j) {
            int u = i + j;
            int v = i - j + n; 
            transformed[u][v] += a[i][j];
        }
    }

    vector<vector<long long>> pref(size + 1, vector<long long>(size + 1, 0));
    for (int i = 1; i <= size; ++i) {
        for (int j = 1; j <= size; ++j) {
            pref[i][j] = transformed[i][j] 
                           + pref[i - 1][j] 
                           + pref[i][j - 1] 
                           - pref[i - 1][j - 1];
        }
    }

    for (int i = 1; i <= n; ++i) {
        for (int j = 1; j <= n; ++j) {
            int u = i + j;
            int v = i - j + n;
            ans[i][j] = calc(pref, u - k, v - k, u + k, v + k);
        }
    }

    for (int i = 1; i <= n; ++i) {
        for (int j = 1; j <= n; ++j) {
            cout << ans[i][j] << " ";
        }
        cout << "\n";
    }

    return 0;
}