#define debug(...)
#define debugArr(...)
#endif
const int MAX_N = 3000;
unordered_map<int, unordered_map<int, unordered_map<int, bool>>> forbidden;
bool visited[MAX_N + 1][MAX_N + 1];
vector<int> adj[MAX_N + 1];

void add_edge(int u, int v) {
    adj[u].push_back(v);
    adj[v].push_back(u);
}

int bfs(int n) {
    queue<tuple<int, int, int>> q; 
    q.push({1, 0, 0});
    visited[1][0] = true;

    while (!q.empty()) {
        auto [current, prev, dist] = q.front();
        q.pop();

        if (current == n) return dist;

        for (int next : adj[current]) {
            if (!forbidden[prev][current][next] && !visited[next][current]) {
                visited[next][current] = true;
                q.push({next, current, dist + 1});
            }
        }
    }

    return -1;
}

int main() {
    int n, m, k;
    cin >> n >> m >> k;

    for (int i = 0; i < m; i++) {
        int u, v;
        cin >> u >> v;
        add_edge(u, v);
    }

    for (int i = 0; i < k; i++) {
        int a, b, c;
        cin >> a >> b >> c;
        forbidden[a][b][c] = true;
    }

    int result = bfs(n);
    cout << result << endl;

    return 0;
}