int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    int n, m;
    cin >> n >> m;

    vector<set<int>> friends(n + 1);

    for (int i = 0; i < m; ++i) {
        int u, v;
        cin >> u >> v;
        friends[u].insert(v);
        friends[v].insert(u);
    }

    for (int i = 1; i <= n; ++i) {
        for (int friend_id : friends[i]) {
            cout << friend_id << " ";
        }
        cout << endl;
    }

    return 0;
}