const int N = 4e6 + 7;

long long a[N], n, pos_max[N], pos_min[N];
long long f[N], g[N];

int main() {
    ios_base::sync_with_stdio(0);
    cin.tie(0);
    cin>>n;
    for(int i = 1; i <= n; i++) cin>>a[i];
    stack<int> stacc;
    for(int i = 1; i <= n; i++) {
        while (!stacc.empty() && a[i] > a[stacc.top()]) stacc.pop();
        if (stacc.empty()) pos_max[i] = 0; else pos_max[i] = stacc.top();
        stacc.push(i);
    }
    stack<long long> stacc2;
    for(int i = 1; i <= n; i++) {
        while (!stacc2.empty() && a[i] < a[stacc2.top()]) stacc2.pop();
        if (stacc2.empty()) pos_min[i] = 0; else pos_min[i] = stacc2.top();
        stacc2.push(i);
    }
    for(int i = 1; i <= n; i++) {
        f[i] = 1LL * a[i] * (i - pos_max[i]) + f[pos_max[i]];
        g[i] = 1LL * a[i] * (i - pos_min[i]) + g[pos_min[i]];
    }
    long long res = 0;
    for(int i = 1; i <= n; i++) res+=f[i] - g[i];
    cout<<res;
    return 0;
}
